INSERT INTO `his_config_info` VALUES (0, 159, 'themall-auth-server', 'DEFAULT_GROUP', '', 'spring:\r\n  servlet:\r\n    multipart:\r\n      max-file-size: 20MB\r\n      max-request-size: 200MB\r\n  jackson:\r\n    date-format: yyyy-MM-dd HH:mm:ss\r\n  #开发期间 关闭缓存\r\n  thymeleaf:\r\n    cache: false\r\n    prefix: classpath*:/templates/\r\n    suffix: .html\r\n\r\n  redis:\r\n    host: 127.0.0.1\r\n    #    password: hjj0127..\r\n    port: 6379\r\n  #配置redis缓存管理器\r\n  cache:\r\n    type: redis\r\n    # 配置了名字，就只能使用自己的名字\r\n    redis:\r\n      # 存活时间\r\n      time-to-live: 3600000\r\n      #前缀\r\n      #      key-prefix: AUTO-CACHE_\r\n      #是否使用前缀 不使用前缀，开启前缀 会把分区名放前面  分区::名字\r\n      use-key-prefix: true\r\n      #是否缓存空值，解决缓存穿透\r\n      cache-null-values: true\r\n#    cache-names:\r\n\r\nmybatis-plus:\r\n  mapper-locations: classpath*:/mapper/**/*.xml\r\n  global-config:\r\n    db-config:\r\n      id-type: auto\r\n      logic-delete-field: flag  # 全局逻辑删除的实体字段名(since 3.3.0,配置后可以忽略不配置步骤2)\r\n      logic-delete-value: 1 # 逻辑已删除值(默认为 1)\r\n      logic-not-delete-value: 0 # 逻辑未删除值(默认为 0)\r\n\r\nlogging:\r\n  level:\r\n    com.theangel.themall: error\r\n\r\n', '95b898239a1904c3bd595aa13cda7f08', '2010-05-05 00:00:00', '2021-08-19 14:34:56', NULL, '113.87.224.2', 'I', '9e9bfac1-f266-4fc4-b220-2c95997c532b');
