mapper.update:
update wms_ware_sku
set stock=#{skuNum}
where sku_id = #{skuId}
  and ware_id = #{wareId}

update wms_ware_sku
set stock_locked=stock_locked + #{num}
where sku_id = #{skuId}
  and ware_id = #{wareId}
  and stock - wms_ware_sku.stock_locked >= #{num}

mapper.select:
select sum(stock - stock_locked)
from wms_ware_sku
where sku_id = #{skuId}
